import React, { useState, useEffect } from "react";
import "./ai_experiment.css";

function AIExperimentScreen({ aiPrediction, onContinue }) {
  const [userPrediction, setUserPrediction] = useState("");
  const [trialOrder, setTrialOrder] = useState([]);
  const [results, setResults] = useState([]);
  const [trialIndex, setTrialIndex] = useState(0);
  const [startTime, setStartTime] = useState(Date.now());

  const contextualInfo = [
    "Child's age: 7 years old<br>Writing experience: Standard classroom instruction<br>Educational history: Recently returned to school after extended illness<br>Other notable comments: Shows increased confidence when using colored pencils or markers",
    "Child's age: 8 years old<br>Writing experience: Learned to write in a different language first<br>Educational history: Malay as second language learner, in mainstream classroom for 1 year<br>Other notable comments: Parents report child is perfectionistic about schoolwork at home",
    "Child's age: 9 years old<br>Writing experience: Experienced different writing instruction approaches with each school change<br>Educational history: Changed schools three times in the past two years<br>Other notable comments: Writing quality varies dramatically depending on interest in topic",
    "Child's age: 7 years old<br>Writing experience: Just beginning formal writing instruction<br>Educational history: Skipped kindergarten (youngest in class)<br>Other notable comments: Often sacrifices quality for speed to have more time for preferred activities",
    "Child's age: 9 years old<br>Writing experience: Consistent instruction since kindergarten<br>Educational history: Performing at grade level across all subjects<br>Other notable comments: Enjoys creative writing and keeps a personal journal",
    "Child's age: 8 years old<br>Writing experience: Traditional classroom instruction<br>Educational history: Excelling in language arts<br>Other notable comments: Frequently volunteers to write answers on the board during class",
    "Child's age: 7 years old<br>Writing experience: Learned writing in kindergarten and first grade<br>Educational history: Is having a hard time catching up with peers in first grade<br>Other notable comments: Dislikes handwriting homework",
    "Child's age: 10 years old<br>Writing experience: Has received extra writing support for two years<br>Educational history: Consistent attendance at the same school since kindergarten<br>Other notable comments: Asks for frequent breaks during writing tasks",
    "Child's age: 8 years old<br>Writing experience: Standard classroom instruction and a writing tutor<br>Educational history: Participates in gifted program for science<br>Other notable comments: Spends more time than peers on writing assignments",
    "Child's age: 10 years old<br>Writing experience: Traditional classroom instruction with some supplemental general help<br>Educational history: Recent transfer student, incomplete records from previous school<br>Other notable comments: Has developed several creative workarounds to minimize writing length",
    "Child's age: 7 years old<br>Writing experience: Regular classroom instruction plus weekly tutoring<br>Educational history: Had a three-month gap in formal education due to family relocation<br>Other notable comments: Teacher has noticed this child often goes back and erases if they donâ€™t think their writing looks good enough",
    "Child's age: 8 years old<br>Writing experience: Began writing instruction in kindergarten<br>Educational history: Receiving special education services since first grade<br>Other notable comments: Expresses anxiety before writing tasks",
    "Child's age: 8 years old<br>Writing experience: Has been using assistive technology for writing<br>Educational history: Recently began receiving additional support services<br>Other notable comments: Parents report child often feels frustrated despite putting in significant effort",
    "Child's age: 9 years old<br>Writing experience: Has had occupational therapy for writing since age 6<br>Educational history: Participates in advanced reading group<br>Other notable comments: Highly motivated and spends extra time practicing writing at home",
    "Child's age: 9 years old<br>Writing experience: Has had consistent writing instruction since age 5<br>Educational history: Recently moved from a different school district with different curriculum<br>Other notable comments: Prefers to dictate stories to parents rather than writing them down",
    "Child's age: 7 years old<br>Writing experience: Regular classroom instruction since kindergarten<br>Educational history: Excels in math and science but struggles with language arts<br>Other notable comments: Becomes emotionally upset when asked to write more than a few sentences"
  ];

  const aiPredictions = [
    "Low risk of dysgraphia",
    "Low risk of dysgraphia",
    "Low risk of dysgraphia",
    "Low risk of dysgraphia",
    "Low risk of dysgraphia",
    "Low risk of dysgraphia",
    "High risk of dysgraphia",
    "High risk of dysgraphia",
    "High risk of dysgraphia",
    "High risk of dysgraphia",
    "High risk of dysgraphia",
    "High risk of dysgraphia",
    "High risk of dysgraphia",
    "High risk of dysgraphia",
    "High risk of dysgraphia",
    "High risk of dysgraphia"
  ];

  const totalTrials = 16;


  // Initialize trials on mount
  useEffect(() => {
    const order = Array.from({ length: totalTrials }, (_, i) => i);
    const shuffled = order.sort(() => Math.random() - 0.5);
    setTrialOrder(shuffled);
    setStartTime(Date.now());
  }, []);

  if (trialOrder.length === 0) return null; // Wait until trials are loaded

  console.log(trialOrder)

  const trialNum = trialOrder[trialIndex];

  // ðŸŸ© Image sources per trial
  const handwritingImg = `images/handwriting_sample_${trialNum + 1}.jpg`;
  const positiveExampleImg = `images/first_prototype_${trialNum + 1}.jpg`;
  const negativeExampleImg = `images/second_prototype_${trialNum + 1}.jpg`;

  const handleContinue = () => {
    const endTime = Date.now();
    const timeDiffSeconds = (endTime - startTime) / 1000;

    const result = {
      imageNumber: trialNum + 1,
      aiPrediction: aiPredictions[trialNum],
      userPrediction,
      contextualInfo: contextualInfo[trialNum],
      timeSeconds: timeDiffSeconds,
    };

    const updatedResults = [...results, result];

    if (trialIndex < totalTrials - 1) {
      setTrialIndex(trialIndex + 1);
      setStartTime(Date.now());
      setUserPrediction("");
      setResults(updatedResults);
      console.log(updatedResults);
    } else {
      // Last trial â€“ submit all
      onContinue(updatedResults);
    }
  };



  return (
    <div className="experiment-container">
      <div className="experiment-box">
        <h1 className="experiment-title">Experiment Trial {trialIndex + 1} / 16</h1>

        <div className="experiment-section">
          <h2>Contextual Information</h2>
          <p>{contextualInfo[trialNum]}</p> 
          <p>Handwriting sample for dysgraphia risk evaluation.</p>
        </div>

        <div className="experiment-image">
          <img
            src={handwritingImg}
            alt={`Handwriting Sample ${trialNum + 1}`}
            className="sample-image"
            style={{ width: '100%', height: 'auto' }}
          />
        </div>

        <div className="experiment-section">
          <h2>AI Prediction</h2>
          <p className="ai-output">{aiPredictions[trialNum]}</p>
          <p className="ai-expl-description">The AI finds images in its dataset that is most similar to the current writing sample that align with its prediction and display 2 below.</p>

          <div className="ai-examples">
            <div className="ai-example-box">
              <p className="example-label">Example: {aiPredictions[trialNum]}</p>
              <img src={positiveExampleImg} alt="Positive example" className="example-image"  style={{ width: '100%', height: 'auto' }} />

            </div>
            <div className="ai-example-box">
              <p className="example-label">Example: {aiPredictions[trialNum]}</p>
              <img src={negativeExampleImg} alt="Negative example" className="example-image"  style={{ width: '100%', height: 'auto' }}/>
            </div>
          </div>
        </div>

        <div className="experiment-section">
          <h2>Your Prediction</h2>
          <div className="radio-group">
            <label>
              <input
                type="radio"
                name="prediction"
                value="low"
                checked={userPrediction === "low"}
                onChange={() => setUserPrediction("low")}
              />
              Low Risk
            </label>
            <label>
              <input
                type="radio"
                name="prediction"
                value="high"
                checked={userPrediction === "high"}
                onChange={() => setUserPrediction("high")}
              />
              High Risk
            </label>
          </div>
        </div>

        <button
          className="experiment-button"
          onClick={handleContinue}
          disabled={!userPrediction}
        >
          {trialIndex < 15 ? "Continue" : "Finish"}
        </button>
      </div>
    </div>
  );
}

export default AIExperimentScreen;
